    private void generarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_generarActionPerformed
        // TODO add your handling code here:
        try {
            GestionDatos gestionDatos = new GestionDatos("Lista.txt");
            Equipo equipo = new Equipo();
            equipo.id = id;
            equipo.local = local.getSelectedItem().toString();
            equipo.visitante = visitante.getSelectedItem().toString();
            equipo.fecha = fecha.getText();
            datos.add(equipo);

            String[] cabeceras = {"Id","Fecha encuentro","Local","Visitante"};
            DefaultTableModel dt = new DefaultTableModel();
            dt.setColumnIdentifiers(cabeceras);
            
            Object[] cell = new Object[dt.getColumnCount()];
            for(Equipo item: datos){
                cell[0] = item.id;
                cell[1] = item.fecha;
                cell[2] = item.local;
                cell[3] = item.visitante;
                dt.addRow(cell);
            }
            jTable1.setModel(dt);
            id++;
            String registros = fecha.getText()+","+local.getSelectedItem()+","+visitante.getSelectedItem();
            gestionDatos.Add(registros);
        } catch (Exception e) {
            return;
        }
        
    }//GEN-LAST:event_generarActionPerformed

